//sistema conversor de temperatura
console.info("Opcion 1 : convertir de Centigrados a Farenheit");
console.info("Opcion 2 : convertir de Farenheit a Centigrados");


let opcion = prompt("Ingrese una opcion");


function convertirCentigrados(gradosC: number): number{
    let resultado: number = 0;
    return resultado = (gradosC * 1.8) + 32;
}

function convertirFarenheit(gradosF: number): number{
    let resultado: number = 0;
    return resultado = (gradosF - 32) / 1.8;
}

if(opcion == "1"){
    let gradosC: number = parseInt(prompt("ingrese los centigrados que quiere convertir a farenheit: "));
    convertirCentigrados(gradosC);   
} 
else if(opcion == "2"){
    let gradosF: number = parseInt(prompt("ingrese los  que quiere farenheit convertir a centigrados: "));
    convertirFarenheit(gradosF);
}
else{
    console.info("Ingrese una opcion valida");
}