import Carroceria from './carroceria';

class Chasis {
    private _longitud: number;
    private _ancho: number;
    private _serie: string;
    private _carroceria: Carroceria ; 
    
    constructor(longitud: number, ancho: number, serie: string, cantidadPuertas: number, numeroSerie: number){
        this._longitud = longitud;
        this._ancho = ancho;
        this._serie = serie;
        this._carroceria = new Carroceria(cantidadPuertas,numeroSerie);
    }
    public mostrar(){
        console.info(`Longitud: ${this._longitud}  Ancho: ${this._ancho} Serie: ${this._serie}`)
        console.info(this._carroceria.mostrar());
    }

}

export default Chasis;