import Chasis from './Chasis';
import Motor from './motor';


class Auto {
    private _marca: string;
    private _modelo: number;
    private _tipo: string;
    private _chasisCarroceria: Chasis[];
    private _motor: Motor[];

    constructor(marca: string, modelo: number, tipo:string){
        this._marca = marca;
        this._modelo = modelo;
        this._tipo = tipo;
        this._chasisCarroceria = [];
        this._motor = [];
    }
    public agregarChasisCarroceria(chasisCarro: Chasis){
        this._chasisCarroceria.push(chasisCarro);
    }

    public agregarMotor(motor: Motor){
        this._motor.push(motor);
    }
    public mostrar() {
        console.info(`Marca: ${this._marca} - Modelo: ${this._modelo} - Tipo: ${this._tipo}`);
        for(let c of this._chasisCarroceria) {
            console.info(c.mostrar());
        }
        for(let c of this._motor) {
            console.info(c.mostrar());
        }
    }
    public arrancar(){
        return 'Ensamblado Exitoso';
    }

}

export default Auto;