class Motor{
    private _marca: string;
    private _capacidad: number;
    private _cilindradas: number;
    private _automatico: boolean;

    constructor(marca: string, capacidad: number, cilindradas: number, automatico:boolean){
        this._marca = marca;
        this._capacidad = capacidad;
        this._cilindradas = cilindradas;
        this._automatico = automatico;
    }

    mostrar(){
        return `Marca: ${this._marca} - Capacidad: ${this._capacidad} 
        - Cilindradas: ${this._cilindradas} - Automatico: ${this._automatico}`; 
    }
}

export default Motor;